package com.yahacode.hiddenblade.tool.utils;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class GeoUtil {

    private static final Logger log = LoggerFactory.getLogger(GeoUtil.class);

    private static double a = 6378245.0;

    private static double ee = 0.00669342162296594323;

    private static double EARTH_RADIUS = 6378.137;

    public static double[] wgs84ToGcj02(double lon, double lat) {
        if (outOfChina(lon, lat)) {
            return new double[]{lon, lat};
        }
        double dLat = transformLat(lon - 105.0, lat - 35.0);
        double dLon = transformLon(lon - 105.0, lat - 35.0);
        double radLat = lat / 180.0 * Math.PI;
        double magic = Math.sin(radLat);
        magic = 1 - ee * magic * magic;
        double sqrtMagic = Math.sqrt(magic);
        dLat = (dLat * 180.0) / ((a * (1 - ee)) / (magic * sqrtMagic) * Math.PI);
        dLon = (dLon * 180.0) / (a / sqrtMagic * Math.cos(radLat) * Math.PI);
        double mgLat = lat + dLat;
        double mgLon = lon + dLon;
        return new double[]{MathUtil.precision(mgLon, 6), MathUtil.precision(mgLat, 6)};
    }

    public static double[] gcj02ToWgs84(double lon, double lat) {
        double d = 0.0000001;
        double longitude = lon;
        double latitude = lat;
        double[] transform;
        do {
            transform = wgs84ToGcj02(longitude, latitude);
            longitude += lon - transform[0];
            latitude += lat - transform[1];
        } while (lon - transform[0] > d || lat - transform[1] > d);
        return new double[]{MathUtil.precision(longitude, 6), MathUtil.precision(latitude, 6)};
    }

    public static double[] bd09ToGcj02(double lon, double lat) {
        double x = lon - 0.0065, y = lat - 0.006;
        double z = Math.sqrt(x * x + y * y) - 0.00002 * Math.sin(y * Math.PI * 3000.0 / 180.0);
        double theta = Math.atan2(y, x) - 0.000003 * Math.cos(x * Math.PI * 3000.0 / 180.0);
        double gcjLon = z * Math.cos(theta);
        double gcjLat = z * Math.sin(theta);
        return new double[]{MathUtil.precision(gcjLon, 6), MathUtil.precision(gcjLat, 6)};
    }

    public static double[] gcj02ToBd09(double lon, double lat) {
        double x = lon, y = lat;
        double z = Math.sqrt(x * x + y * y) + 0.00002 * Math.sin(y * Math.PI * 3000.0 / 180.0);
        double theta = Math.atan2(y, x) + 0.000003 * Math.cos(x * Math.PI * 3000.0 / 180.0);
        double bdLon = z * Math.cos(theta) + 0.0065;
        double bdLat = z * Math.sin(theta) + 0.006;
        return new double[]{MathUtil.precision(bdLon, 6), MathUtil.precision(bdLat, 6)};
    }

    private static double transformLat(double x, double y) {
        double ret = -100.0 + 2.0 * x + 3.0 * y + 0.2 * y * y + 0.1 * x * y
                + 0.2 * Math.sqrt(Math.abs(x));
        ret += (20.0 * Math.sin(6.0 * x * Math.PI) + 20.0 * Math.sin(2.0 * x * Math.PI)) * 2.0 / 3.0;
        ret += (20.0 * Math.sin(y * Math.PI) + 40.0 * Math.sin(y / 3.0 * Math.PI)) * 2.0 / 3.0;
        ret += (160.0 * Math.sin(y / 12.0 * Math.PI) + 320 * Math.sin(y * Math.PI / 30.0)) * 2.0 / 3.0;
        return ret;
    }

    private static double transformLon(double x, double y) {
        double ret = 300.0 + x + 2.0 * y + 0.1 * x * x + 0.1 * x * y + 0.1 * Math.sqrt(Math.abs(x));
        ret += (20.0 * Math.sin(6.0 * x * Math.PI) + 20.0 * Math.sin(2.0 * x * Math.PI)) * 2.0 / 3.0;
        ret += (20.0 * Math.sin(x * Math.PI) + 40.0 * Math.sin(x / 3.0 * Math.PI)) * 2.0 / 3.0;
        ret += (150.0 * Math.sin(x / 12.0 * Math.PI) + 300.0 * Math.sin(x / 30.0 * Math.PI)) * 2.0 / 3.0;
        return ret;
    }

    public static boolean outOfChina(double lon, double lat) {
        return !inChina(lon, lat);
    }

    public static boolean inChina(double lon, double lat) {
        try {
            int lonIdx = (int) ((lon - 73.0D) / 0.5D);
            int latIdx = (int) ((lat - 3.5D) / 0.5D);
            if (latIdx >= 0 && latIdx < 101 && lonIdx >= 0 && lonIdx < 124) {
                int pos = 124 * latIdx + lonIdx;
                char c = "".charAt(pos);
                return c == '1';
            } else {
                return false;
            }
        } catch (Throwable e) {
            log.warn("calc in China fail, lon:{}, lat:{}", lon, lat, e);
            return true;
        }
    }

    public static double distance(double lon1, double lat1, double lon2, double lat2) {
        double a = rad(lat1) - rad(lat2);
        double b = rad(lon1) - rad(lon2);
        double s = 2 * Math.asin(Math.sqrt(Math.pow(Math.sin(a / 2), 2) + Math.cos(rad(lat1)) * Math.cos(rad(lat2)) * Math.pow(Math.sin(b / 2), 2)));
        s = s * EARTH_RADIUS;
        s = Math.round(s * 10000d) / 10000d;
        s = s * 1000;
        return s;
    }

    public static double distance(String location1, String location2) {
        double[] coordinates1 = toCoordinates(location1);
        double[] coordinates2 = toCoordinates(location2);
        return distance(coordinates1[0], coordinates1[1], coordinates2[0], coordinates2[1]);
    }

    private static double[] toCoordinates(String location) {
        String[] coordinates = location.split(",");
        return new double[]{Double.valueOf(coordinates[0]), Double.valueOf(coordinates[1])};
    }

    private static double rad(double d) {
        return d * Math.PI / 180.0;
    }
}
